import React from 'react';

import {
  SafeAreaView,
  StyleSheet,
  ScrollView,
  View,
  Text,
  StatusBar,
  Appearance,
  Button,
  TouchableHighlight,
  FlatList,
} from 'react-native';

import Recipe from '../Recipe';
import RecipeScreen from './RecipeScreen';
import * as recipes from '../../recipes/recipes.json';

import VideoScreen from './VideoScreen';

import { createStackNavigator } from '@react-navigation/stack';
const Stack = createStackNavigator();

export default function HomeScreen() {
  return (
    <Stack.Navigator
      initialRouteName="Home"
      screenOptions={{
        headerStyle: { backgroundColor: '#2980b9' },
        headerTintColor: 'white',
        headerTitleStyle: {
          fontWeight: 'bold',
          fontFamily: 'Poppins-Regular',
        },
      }}>
      <Stack.Screen
        name="Home"
        component={HomeComponent}
        options={{ headerTitleAlign: 'left' }}
      />
      <Stack.Screen
        name="RecipeScreen"
        component={RecipeScreen}
        options={{ title: 'Recipe' }}
      />
      <Stack.Screen
        name="VideoScreen"
        component={VideoScreen}
        options={{ title: 'VideoScreen' }}
      />
    </Stack.Navigator>
  );
}

function HomeComponent({ navigation }) {
  let recipesList = [];
  let objLen = Object.keys(recipes).length - 1;

  for (let i = 0; i < objLen; i++) {
    recipesList.push({
      id: i,
      name: recipes[i].name,
      img_uri: recipes[i].img_uri,
      short_desc: recipes[i].short_desc,
    });
  }

  const renderItem = ({ item }) => (
    <Recipe
      plateName={item.name}
      image={item.img_uri}
      description={item.short_desc}
      navigation={navigation}
    />
  );

  return (
    <View style={{ flex: 1 }}>
      <StatusBar backgroundColor="#2980b9" barStyle="light-content" />
      <SafeAreaView style={styles.recipesContainer}>
        <FlatList
          contentContainerStyle={{ paddingBottom: 20 }}
          data={recipesList}
          renderItem={renderItem}
          keyExtractor={(item) => item.id.toString()}
        />
        <Button onPress={() => navigation.navigate('VideoScreen')} title="Go to video screen" />
      </SafeAreaView>
    </View>
  );
}

const styles = StyleSheet.create({
  recipesContainer: {
    flex: 1,
    height: '85%',
    marginTop: 15,
  },
});
